-- Courses( Master table )-- Students ( transaction Table)-- Trainers( Self Join use cases)CREATE TABLE MyCourses (    CourseId INT PRIMARY KEY,    CourseName VARCHAR(100));CREATE TABLE My_Students (    StudentId INT PRIMARY KEY,    StudentName VARCHAR(50),    CourseId INT);CREATE TABLE Trainers (    TrainerId INT PRIMARY KEY,    TrainerName VARCHAR(50),    ManagerId INT);INSERT INTO MyCourses VALUES(101,'Full stack'),(102,'Azure'),(103,'c sharp ');INSERT INTO My_Students VALUES(1,'haritha',101),(2,'lahari',102),(3,'anjali',101),(4,'hema',null);INSERT INTO Trainers VALUES(1,'arjun',null),(2,'ravi',1),(3,'pavan',1),(4,'navatha',2);SELECT * FROM MyCourses;SELECT * FROM My_Students;SELECT * FROM Trainers;--Types of Joins-- It will return common value in both the table-- Student who have enrolled in a courseSELECT  s.StudentName,s.StudentId, c.CourseName, c.CourseIdFROM My_Students sINNER JOIN MyCourses cON s.CourseID = c.CourseID;--Left join : will retun all left table record + matched right records--List of all students including those not enrolled SELECT s.StudentName,s.StudentId, c.CourseName, c.CourseIdFROM My_Students sLEFT JOIN MyCourses cON s.CourseId = c.CourseId;-- RIGHT Join : All right reocrd +  matching reocord-- It will display all courses if if there is no enrolled studentSELECT s.StudentName,s.StudentId, c.CourseName, c.CourseIdFROM My_Students sRIGHT JOIN MyCourses cON s.CourseId = c.CourseId;-- SELF join :  Trainer - manager -  hierarchy-- univerity _ HOD- Faculty hierarchy SELECT     t1.TrainerName AS Trainers,    t2.TrainerName AS Manager    FROM Trainers t1    LEFT JOIN Trainers t2    ON t1.ManagerId = t2.TrainerId;--table  joins to itself -- CROSS join : Itreturns cartesian prodcuct-- All possible studnt and course combination -- (Used in testing and simulations)SELECT s.studentname, c.CoursenameFROM My_Students sCROSS JOIN MyCourses c;-- Full Outer Join -- It return all the records frm both table -- Complete Enrollement Audit SELECT s.StudentName,s.StudentId, c.CourseName, c.CourseIdFROM My_Students sFULL OUTER JOIN MyCourses cON s.CourseId = c.CourseId;